CREATE VIEW PROKAS.V_KD_VORG_POS  (    MANDANTENNR,    APO_NR,    GP_VORG_NR,
   GP_VORG_POS_NR,    ART_TYP_NR,    STATUS,    GP_VORG_POS_TXT,    ART_MNG,
   ART_MNG_ALLE,    ART_MNG_TEIL_ZL,    GP_VORG_POS_PREIS,    MWST_KENNZ,
   BRUTTO_ZW_PREIS,    ART_PREIS_KENNZ,    NAT_RAB_MNG,    NAT_RAB_MNG_TZ,
   POS_WB_STAT,    ERL_GRP_NR,    EK_ZW_PREIS,    ABVK_POS_AMO_FLG,   
VORG_POS_TXT_FLG,    VORG_POS_BEZ_FLG,    VORG_POS_DUMMY_FLG,    POS_FAKT_FLG,
   POS_LIEFER_FLG,    ZUS_BSTG_MNG,    GESCHAEFTS_KENNZ,    KASS_STATUS,
   WGRP_KENNZ,    ZUZ_PREIS,    EANT_PREIS,    REZ_NR,    MWST_PREIS, 
  LISTEN_VK,    LISTEN_EK,    HERST_ABG_PREIS,    BASIS_PREIS,    ZU_ABSCHLAG,
   BSTD_WERT_BUCH,    VEK,    BWSTATISTIK,    BWMENGENBUCHUNG,    STORNO_KENNZ,
   STORNO_VORGNR_O,    STORNO_VORGPOSNR_O,    STORNO_VORGNR_S,    STORNO_VORGPOSNR_S,
   KASSENRABATT  )  AS  SELECT    0,    0,    a.GP_VORG_NR,    a.GP_VORG_POS_NR,
   a.ART_TYP_NR,    a.STATUS,    a.GP_VORG_POS_TXT,    CASE WHEN (a.KASS_STATUS
IN ('5','51','52','53','54','7','71','72','73','74','8')              
OR d.ART_TYP_TYP IN ('D','U','Q') OR a.BSTD_WERT_BUCH = ' W')         THEN
0     ELSE a.ART_MNG end,    a.ART_MNG,    a.ART_MNG_TEIL_ZL,    a.GP_VORG_POS_PREIS,
   a.MWST_KENNZ,    a.BRUTTO_ZW_PREIS,    a.ART_PREIS_KENNZ,    a.NAT_RAB_MNG,
   a.NAT_RAB_MNG_TZ,    a.POS_WB_STAT,    a.ERL_GRP_NR,    a.EK_ZW_PREIS,
   a.ABVK_POS_AMO_FLG,    a.VORG_POS_TXT_FLG,    a.VORG_POS_BEZ_FLG,  
 a.VORG_POS_DUMMY_FLG,    a.POS_FAKT_FLG,    a.POS_LIEFER_FLG,    a.ZUS_BSTG_MNG,
   a.GESCHAEFTS_KENNZ,    a.KASS_STATUS,    a.WGRP_KENNZ,    a.ZUZ_PREIS,
   a.EANT_PREIS,    a.REZ_NR,    a.MWST_PREIS,    a.LISTEN_VK,    a.LISTEN_EK,
   a.HERST_ABG_PREIS,    a.BASIS_PREIS,    a.ZU_ABSCHLAG,    BSTD_WERT_BUCH,
   a.EK_ZW_PREIS/case when a.ART_MNG<>0     then COALESCE(a.ART_MNG/case
when COALESCE(ART_MNG_TEIL_ZL,1) = 0 then 1 else ART_MNG_TEIL_ZL end,1)
   else 1 end,    case when            KASS_STATUS NOT IN ('5','52','54','7','71','72','73','74','8')
           AND BSTD_WERT_BUCH <> 'B '           AND TRANSLATE(GP_VORG_POS_TXT)
NOT LIKE 'AUFL%SUNG%OFF%'          then 'J'         else              'N'
        end,    case when            KASS_STATUS NOT IN ('5','51','52','53','54','7','71','72','73','74','8')
           AND BSTD_WERT_BUCH <> ' W'           AND a.ABVK_POS_AMO_FLG
<> '3'         then 'J'         else              'N'         end,    CASE
WHEN COALESCE(b.GP_VORG_NR,0) > 0 then 'O'          WHEN COALESCE(c.RET_VORG_NR,0)
> 0 then 'S'          ELSE ''    end,    COALESCE(c.GP_VORG_NR,0),    COALESCE(c.GP_VORG_POS_NR,0),
   COALESCE(b.RET_VORG_NR,0),    COALESCE(b.RET_VORG_POS_NR,0),    CASE
WHEN KASS_STATUS IN ('1','2','6','9','21') THEN              a.ART_MNG
* (select INTEGER(COALESCE(pf.KDV_POS_FELD_WERT,'0'))/100.00          
       FROM PROKAS.B_KDV_POS_FELD pf                  WHERE a.GP_VORG_NR=pf.GP_VORG_NR
                       AND a.GP_VORG_POS_NR=pf.GP_VORG_POS_NR         
              AND pf.KDV_POS_FELD_NR=115)          ELSE           0.00
   END  FROM PROKAS.E_KD_VORG_POS a    LEFT JOIN PROKAS.B_ABVK_RET b  
   ON a.GP_VORG_NR=b.GP_VORG_NR                                       
   AND a.GP_VORG_POS_NR=b.GP_VORG_POS_NR    LEFT JOIN PROKAS.B_ABVK_RET
c      ON a.GP_VORG_NR=c.RET_VORG_NR                                  
        AND a.GP_VORG_POS_NR=c.RET_VORG_POS_NR    LEFT JOIN PROKAS.E_ART_TYP
d       ON d.ART_TYP_NR = a.ART_TYP_NR  ;

