   b.BR_MW1_ZW_SU_PREIS,    b.BR_MW1_ZW_SU_PREIS-b.MWST1_ZW_SU_PREIS, 
  b.GP_VORG_TXT,    b.GP_VORG_TXT_FLG,    coalesce((select sum(EK_ZW_PREIS)
from prokas.e_kd_vorg_pos c where b.GP_VORG_NR = c.GP_VORG_NR and c.MWST_KENNZ='0'),0.00),
   coalesce((Select KD_VORG_FELD_WERT from PROKAS.B_KD_VORG_FELD d where
