a, PROKAS.E_KD_VORG b  WHERE  a.GP_VORG_NR = b.GP_VORG_NR  and b.MWST1_ZW_SU_PREIS
<> 0  UNION  SELECT    'H',    b.GP_VORG_NR,    b.MWST2_ZW_SU_PREIS,  
 b.BR_MW2_ZW_SU_PREIS,    b.BR_MW2_ZW_SU_PREIS-b.MWST2_ZW_SU_PREIS,   
b.GP_VORG_TXT,    b.GP_VORG_TXT_FLG,    coalesce((select sum(EK_ZW_PREIS)
