   coalesce((Select KD_VORG_FELD_WERT from PROKAS.B_KD_VORG_FELD d where
b.GP_VORG_NR = d.GP_VORG_NR and KD_VORG_FELD_NR = 147),'N')  FROM  PROKAS.E_KD_RECH
a, PROKAS.E_KD_VORG b  WHERE  a.GP_VORG_NR = b.GP_VORG_NR  and b.MWST2_ZW_SU_PREIS
<> 0  UNION  SELECT    'O',    b.GP_VORG_NR,    0.00,    DOUBLE(DECIMAL(coalesce(c.KD_VORG_FELD_WERT,'0.00'),10,2)),
   DOUBLE(DECIMAL(coalesce(c.KD_VORG_FELD_WERT,'0.00'),10,2)),    b.GP_VORG_TXT,
   b.GP_VORG_TXT_FLG,    coalesce((select sum(EK_ZW_PREIS) from prokas.e_kd_vorg_pos
c where b.GP_VORG_NR = c.GP_VORG_NR and c.MWST_KENNZ='2'),0.00),    coalesce((Select
d.KD_VORG_FELD_WERT from PROKAS.B_KD_VORG_FELD d where d.GP_VORG_NR = b.GP_VORG_NR
and KD_VORG_FELD_NR = 147),'N')  FROM  PROKAS.E_KD_RECH a, PROKAS.E_KD_VORG
b  inner join PROKAS.B_KD_VORG_FELD C on c.GP_VORG_NR = b.GP_VORG_NR AND
