   END     AS TRANSAKT_STATUS,    '',    A.AUSTELLUNGS_DATUM,    '',  
 '',    B.REZ_NR,    C.DRUCK_DATUM,    C.BED_NR,    C.BED_KURZ,    C.APL_NR,
