    ANF.BED_NR,     ANF.BED_KURZ,     ANF.CLIENT,     ANF.ACTION,     ANF.STATUS,
    ANF.PACKUNG_STATUS,     ANF.CHECK_STATUS,     JOB.ANFRAGE_NR,     JOB.ANZAHL,
    JOB.SECPHARM_URL,     JOB.SECPHARM_REQUEST,     JOB.SECPHARM_STX, 
   PACK.CTX,     PACK.PC,     PACK.PCS,     PACK.DMC,     PACK.SN,    
PACK.LOT,     PACK.EXP,     PACK.HID,     PACK.COD,     PACK.SUCCESS, 
   PACK.VERIBEGINN_PFL_132,     PACK.SECURPHARM_PIL_144,     PACK.HOCHLADEDATUM_145,
